mapper.select:
SELECT *
FROM PM_USER
WHERE USER_ID = #{user_id}
AND USER_PW = #{user_pw}

mapper.insert:
INSERT INTO PM_USER (
	USER_ID, USER_PW, LAST_UPDATE
) VALUES (
	#{user_id}, #{user_pw}, CURDATE()
)

mapper.update:
UPDATE PM_USER
SET USER_PW = #{user_pw}
WHERE USER_ID = #{user_id}

mapper.select:
SELECT *
FROM PM_USER
WHERE USER_SEQ IN (
	SELECT USER_SEQ
	FROM PM_CHARACTER
	WHERE CHARACTER_NAME = #{character_name}
)
AND USER_ID = #{user_id}
