mapper.insert:

	SELECT IFNULL(MAX(SEQUENCE), 0)+1 FROM PM_MANAGEMENT WHERE CHARACTER_SEQ = #{character_seq};

INSERT INTO PM_MANAGEMENT (
	MANAGEMENT_CODE, CHARACTER_SEQ, COMPLETE_COUNT, SEQUENCE
) VALUES (
	#{management_code}, #{character_seq}, #{complete_count}, #{sequence}
)

mapper.delete:
DELETE FROM PM_MANAGEMENT
WHERE CHARACTER_SEQ = #{character_seq}
AND MANAGEMENT_CODE = #{management_code}
